SELECT B.WRITER_ID AS USER_ID, U.NICKNAME, U.CITY||' '||U.STREET_ADDRESS1||' '||U.STREET_ADDRESS2 AS 전체주소, REGEXP_REPLACE(U.TLNO, '(.{3})(.+)(.{4})','\1-\2-\3') AS 전화번호   from USED_GOODS_USER U INNER JOIN USED_GOODS_BOARD B ON U.USER_ID = B.WRITER_ID GROUP BY B.WRITER_ID, U.NICKNAME, U.CITY||' '||U.STREET_ADDRESS1||' '||U.STREET_ADDRESS2, REGEXP_REPLACE(U.TLNO, '(.{3})(.+)(.{4})','\1-\2-\3') HAVING COUNT(*) > 2 ORDER BY USER_ID DESC;
